00346         void ProcessBezierPathInput (Event e) {
00347             // Find which handle mouse is over. Start by looking at previous handle index first, as most likely to still be closest to mouse
00348             int previousMouseOverHandleIndex = (mouseOverHandleIndex == -1) ? 0 : mouseOverHandleIndex;
00349             mouseOverHandleIndex = -1;
00350             for (int i = 0; i < bezierPath.NumPoints; i += 3) {
00351 
00352                 int handleIndex = (previousMouseOverHandleIndex + i) % bezierPath.NumPoints;
00353                 float handleRadius = GetHandleDiameter (globalDisplaySettings.anchorSize * data.bezierHandleScale, bezierPath[handleIndex]) / 2f;
00354                 Vector3 pos = MathUtility.TransformPoint (bezierPath[handleIndex], creator.transform, bezierPath.Space);
00355                 float dst = HandleUtility.DistanceToCircle (pos, handleRadius);
00356                 if (dst == 0) {
00357                     mouseOverHandleIndex = handleIndex;
00358                     break;
00359                 }
00360             }
00361 
00362             // Shift-left click (when mouse not over a handle) to split or add segment
00363             if (mouseOverHandleIndex == -1) {
00364                 if (e.type == EventType.MouseDown && e.button == 0 && e.shift) {
00365                     UpdatePathMouseInfo ();
00366                     // Insert point along selected segment
00367                     if (selectedSegmentIndex != -1 && selectedSegmentIndex < bezierPath.NumSegments) {
00368                         Vector3 newPathPoint = pathMouseInfo.closestWorldPointToMouse;
00369                         newPathPoint = MathUtility.InverseTransformPoint (newPathPoint, creator.transform, bezierPath.Space);
00370                         Undo.RecordObject (creator, "Split segment");
00371                         bezierPath.SplitSegment (newPathPoint, selectedSegmentIndex, pathMouseInfo.timeOnBezierSegment);
00372                     }
00373                     // If path is not a closed loop, add new point on to the end of the path
00374                     else if (!bezierPath.IsClosed) {
00375                         // insert new point at same dst from scene camera as the point that comes before it (for a 3d path)
00376                         float dstCamToEndpoint = (Camera.current.transform.position - bezierPath[bezierPath.NumPoints - 1]).magnitude;
00377                         Vector3 newPathPoint = MouseUtility.GetMouseWorldPosition (bezierPath.Space, dstCamToEndpoint);
00378                         newPathPoint = MathUtility.InverseTransformPoint (newPathPoint, creator.transform, bezierPath.Space);
00379 
00380                         Undo.RecordObject (creator, "Add segment");
00381                         if (e.control || e.command) {
00382                             bezierPath.AddSegmentToStart (newPathPoint);
00383                         } else {
00384                             bezierPath.AddSegmentToEnd (newPathPoint);
00385                         }
00386 
00387                     }
00388 
00389                 }
00390             }
00391 
00392             // Control click or backspace/delete to remove point
00393             if (e.keyCode == KeyCode.Backspace || e.keyCode == KeyCode.Delete || ((e.control || e.command) && e.type == EventType.MouseDown && e.button == 0)) {
00394 
00395                 if (mouseOverHandleIndex != -1) {
00396                     Undo.RecordObject (creator, "Delete segment");
00397                     bezierPath.DeleteSegment (mouseOverHandleIndex);
00398                     if (mouseOverHandleIndex == handleIndexToDisplayAsTransform) {
00399                         handleIndexToDisplayAsTransform = -1;
00400                     }
00401                     mouseOverHandleIndex = -1;
00402                     Repaint ();
00403                 }
00404             }
00405 
00406             // Holding shift and moving mouse (but mouse not over a handle/dragging a handle)
00407             if (draggingHandleIndex == -1 && mouseOverHandleIndex == -1) {
00408                 bool shiftDown = e.shift && !shiftLastFrame;
00409                 if (shiftDown || ((e.type == EventType.MouseMove || e.type == EventType.MouseDrag) && e.shift)) {
00410 
00411                     UpdatePathMouseInfo ();
00412 
00413                     if (pathMouseInfo.mouseDstToLine < segmentSelectDistanceThreshold) {
00414                         if (pathMouseInfo.closestSegmentIndex != selectedSegmentIndex) {
00415                             selectedSegmentIndex = pathMouseInfo.closestSegmentIndex;
00416                             HandleUtility.Repaint ();
00417                         }
00418                     } else {
00419                         selectedSegmentIndex = -1;
00420                         HandleUtility.Repaint ();
00421                     }
00422 
00423                 }
00424             }
00425 
00426             shiftLastFrame = e.shift;
00427 
00428         }
