00501         void DrawHandle (int i) {
00502             Vector3 handlePosition = MathUtility.TransformPoint (bezierPath[i], creator.transform, bezierPath.Space);
00503 
00504             float anchorHandleSize = GetHandleDiameter (globalDisplaySettings.anchorSize * data.bezierHandleScale, bezierPath[i]);
00505             float controlHandleSize = GetHandleDiameter (globalDisplaySettings.controlSize * data.bezierHandleScale, bezierPath[i]);
00506 
00507             bool isAnchorPoint = i % 3 == 0;
00508             bool isInteractive = isAnchorPoint || bezierPath.ControlPointMode != BezierPath.ControlMode.Automatic;
00509             float handleSize = (isAnchorPoint) ? anchorHandleSize : controlHandleSize;
00510             bool doTransformHandle = i == handleIndexToDisplayAsTransform;
00511 
00512             PathHandle.HandleColours handleColours = (isAnchorPoint) ? splineAnchorColours : splineControlColours;
00513             if (i == handleIndexToDisplayAsTransform) {
00514                 handleColours.defaultColour = (isAnchorPoint) ? globalDisplaySettings.anchorSelected : globalDisplaySettings.controlSelected;
00515             }
00516             var cap = capFunctions[(isAnchorPoint) ? globalDisplaySettings.anchorShape : globalDisplaySettings.controlShape];
00517             PathHandle.HandleInputType handleInputType;
00518             handlePosition = PathHandle.DrawHandle (handlePosition, bezierPath.Space, isInteractive, handleSize, cap, handleColours, out handleInputType, i);
00519 
00520             if (doTransformHandle) {
00521                 // Show normals rotate tool 
00522                 if (data.showNormals && Tools.current == Tool.Rotate && isAnchorPoint && bezierPath.Space == PathSpace.xyz) {
00523                     Handles.color = handlesStartCol;
00524 
00525                     int attachedControlIndex = (i == bezierPath.NumPoints - 1) ? i - 1 : i + 1;
00526                     Vector3 dir = (bezierPath[attachedControlIndex] - handlePosition).normalized;
00527                     float handleRotOffset = (360 + bezierPath.GlobalNormalsAngle) % 360;
00528                     anchorAngleHandle.radius = handleSize * 3;
00529                     anchorAngleHandle.angle = handleRotOffset + bezierPath.GetAnchorNormalAngle (i / 3);
00530                     Vector3 handleDirection = Vector3.Cross (dir, Vector3.up);
00531                     Matrix4x4 handleMatrix = Matrix4x4.TRS (
00532                         handlePosition,
00533                         Quaternion.LookRotation (handleDirection, dir),
00534                         Vector3.one
00535                     );
00536 
00537                     using (new Handles.DrawingScope (handleMatrix)) {
00538                         // draw the handle
00539                         EditorGUI.BeginChangeCheck ();
00540                         anchorAngleHandle.DrawHandle ();
00541                         if (EditorGUI.EndChangeCheck ()) {
00542                             Undo.RecordObject (creator, "Set angle");
00543                             bezierPath.SetAnchorNormalAngle (i / 3, anchorAngleHandle.angle - handleRotOffset);
00544                         }
00545                     }
00546 
00547                 } else {
00548                     handlePosition = Handles.DoPositionHandle (handlePosition, Quaternion.identity);
00549                 }
00550 
00551             }
00552 
00553             switch (handleInputType) {
00554                 case PathHandle.HandleInputType.LMBDrag:
00555                     draggingHandleIndex = i;
00556                     handleIndexToDisplayAsTransform = -1;
00557                     Repaint ();
00558                     break;
00559                 case PathHandle.HandleInputType.LMBRelease:
00560                     draggingHandleIndex = -1;
00561                     handleIndexToDisplayAsTransform = -1;
00562                     Repaint ();
00563                     break;
00564                 case PathHandle.HandleInputType.LMBClick:
00565                     draggingHandleIndex = -1;
00566                     if (Event.current.shift) {
00567                         handleIndexToDisplayAsTransform = -1; // disable move tool if new point added
00568                     } else {
00569                         if (handleIndexToDisplayAsTransform == i) {
00570                             handleIndexToDisplayAsTransform = -1; // disable move tool if clicking on point under move tool
00571                         } else {
00572                             handleIndexToDisplayAsTransform = i;
00573                         }
00574                     }
00575                     Repaint ();
00576                     break;
00577                 case PathHandle.HandleInputType.LMBPress:
00578                     if (handleIndexToDisplayAsTransform != i) {
00579                         handleIndexToDisplayAsTransform = -1;
00580                         Repaint ();
00581                     }
00582                     break;
00583             }
00584 
00585             Vector3 localHandlePosition = MathUtility.InverseTransformPoint (handlePosition, creator.transform, bezierPath.Space);
00586 
00587             if (bezierPath[i] != localHandlePosition) {
00588                 Undo.RecordObject (creator, "Move point");
00589                 bezierPath.MovePoint (i, localHandlePosition);
00590 
00591             }
00592 
00593         }
00594 
00595         #endregion
