00603         void OnEnable () {
00604             creator = (PathCreator) target;
00605             bool in2DEditorMode = EditorSettings.defaultBehaviorMode == EditorBehaviorMode.Mode2D;
00606             creator.InitializeEditorData (in2DEditorMode);
00607 
