00617         }
00618 
00619         void SetTransformState (bool initialize = false) {
00620             Transform t = creator.transform;
00621             if (!initialize) {
00622                 if (transformPos != t.position || t.localScale != transformScale || t.rotation != transformRot) {
00623                     data.PathTransformed ();
00624                 }
00625             }
00626             transformPos = t.position;
00627             transformScale = t.localScale;
00628             transformRot = t.rotation;
00629         }
