00644         void LoadDisplaySettings () {
00645             globalDisplaySettings = GlobalDisplaySettings.Load ();
00646 
00647             capFunctions = new Dictionary<GlobalDisplaySettings.HandleType, Handles.CapFunction> ();
00648             capFunctions.Add (GlobalDisplaySettings.HandleType.Circle, Handles.CylinderHandleCap);
00649             capFunctions.Add (GlobalDisplaySettings.HandleType.Sphere, Handles.SphereHandleCap);
00650             capFunctions.Add (GlobalDisplaySettings.HandleType.Square, Handles.CubeHandleCap);
00651         }
00652 
00653         void UpdateGlobalDisplaySettings () {
00654             var gds = globalDisplaySettings;
00655             splineAnchorColours = new PathHandle.HandleColours (gds.anchor, gds.anchorHighlighted, gds.anchorSelected, gds.handleDisabled);
00656             splineControlColours = new PathHandle.HandleColours (gds.control, gds.controlHighlighted, gds.controlSelected, gds.handleDisabled);
00657 
00658             anchorAngleHandle.fillColor = new Color (1, 1, 1, .05f);
00659             anchorAngleHandle.wireframeColor = Color.grey;
00660             anchorAngleHandle.radiusHandleColor = Color.clear;
00661             anchorAngleHandle.angleHandleColor = Color.white;
00662         }
00663 
00664         void ResetState () {
00665             selectedSegmentIndex = -1;
00666             draggingHandleIndex = -1;
00667             mouseOverHandleIndex = -1;
00668             handleIndexToDisplayAsTransform = -1;
00669             hasUpdatedScreenSpaceLine = false;
00670             hasUpdatedNormalsVertexPath = false;
00671 
00672             bezierPath.OnModified -= OnPathModifed;
00673             bezierPath.OnModified += OnPathModifed;
00674 
00675             SceneView.RepaintAll ();
00676             EditorApplication.QueuePlayerLoopUpdate ();
00677         }
00678 
00679         void OnPathModifed () {
00680             hasUpdatedScreenSpaceLine = false;
