00686         void RepaintUnfocusedSceneViews () {
00687             // If multiple scene views are open, repaint those which do not have focus.
00688             if (SceneView.sceneViews.Count > 1) {
00689                 foreach (SceneView sv in SceneView.sceneViews) {
00690                     if (EditorWindow.focusedWindow != (EditorWindow) sv) {
00691                         sv.Repaint ();
00692                     }
00693                 }
00694             }
00695         }
