00697         void UpdatePathMouseInfo () {
00698 
00699             if (!hasUpdatedScreenSpaceLine || (screenSpaceLine != null && screenSpaceLine.TransformIsOutOfDate ())) {
00700                 screenSpaceLine = new ScreenSpacePolyLine (bezierPath, creator.transform, screenPolylineMaxAngleError, screenPolylineMinVertexDst);
00701                 hasUpdatedScreenSpaceLine = true;
00702             }
00703             pathMouseInfo = screenSpaceLine.CalculateMouseInfo ();
00704         }
