00706         float GetHandleDiameter (float diameter, Vector3 handlePosition) {
00707             float scaledDiameter = diameter * constantHandleScale;
00708             if (data.keepConstantHandleSize) {
00709                 scaledDiameter *= HandleUtility.GetHandleSize (handlePosition) * 2.5f;
00710             }
00711             return scaledDiameter;
00712         }
