00016     // Internally we serialize an Object to the SceneAsset which only exists at editor time.
00017     // Any time the object is serialized, we store the path provided by this Asset (assuming it was valid).
00018     //
00019     // This means that, come build time, the string path of the scene asset is always already stored, which if 
00020     // the scene was added to the build settings means it can be loaded.
00021     //
00022     // It is up to the user to ensure the scene exists in the build settings so it is loadable at runtime.
00023     // To help with this, a custom PropertyDrawer displays the scene build settings state.
00024     //
00025     //  Known issues:
00026     // - When reverting back to a prefab which has the asset stored as null, Unity will show the property 
00027     // as modified despite having just reverted. This only happens on the fist time, and reverting again fix it. 
00028     // Under the hood the state is still always valid and serialized correctly regardless.
00029 
00033     [Serializable]
00034     public class SceneReference : ISerializationCallbackReceiver
00035     {
00036 #if UNITY_EDITOR
00037         // What we use in editor to select the scene
00038         [SerializeField] private Object sceneAsset;
00039         private bool IsValidSceneAsset
00040         {
00041             get
00042             {
00043                 if (!sceneAsset) return false;
00044 
00045                 return sceneAsset is SceneAsset;
00046             }
00047         }
00048 #endif
00049 
00050         // This should only ever be set during serialization/deserialization!
00051         [SerializeField]
00052         private string scenePath = string.Empty;
00053 
00054         // Use this when you want to actually have the scene path
00055         public string ScenePath
00056         {
00057             get
00058             {
00059 #if UNITY_EDITOR
00060                 // In editor we always use the asset's path
00061                 return GetScenePathFromAsset();
00062 #else
00063             // At runtime we rely on the stored path value which we assume was serialized correctly at build time.
00064             // See OnBeforeSerialize and OnAfterDeserialize
00065             return scenePath;
00066 #endif
00067             }
00068             set
00069             {
00070                 scenePath = value;
00071 #if UNITY_EDITOR
00072                 sceneAsset = GetSceneAssetFromPath();
00073 #endif
00074             }
00075         }
00076 
00077         public static implicit operator string(SceneReference sceneReference)
00078         {
00079             return sceneReference.ScenePath;
00080         }
00081 
00082         // Called to prepare this data for serialization. Stubbed out when not in editor.
00083         public void OnBeforeSerialize()
00084         {
00085 #if UNITY_EDITOR
00086             HandleBeforeSerialize();
00087 #endif
00088         }
00089 
00090         // Called to set up data for deserialization. Stubbed out when not in editor.
00091         public void OnAfterDeserialize()
00092         {
00093 #if UNITY_EDITOR
00094             // We sadly cannot touch assetdatabase during serialization, so defer by a bit.
00095             EditorApplication.update += HandleAfterDeserialize;
00096 #endif
00097         }
00098 
00099 #if UNITY_EDITOR
00100         private SceneAsset GetSceneAssetFromPath()
00101         {
00102             return string.IsNullOrEmpty(scenePath) ? null : AssetDatabase.LoadAssetAtPath<SceneAsset>(scenePath);
00103         }
00104 
00105         private string GetScenePathFromAsset()
00106         {
00107             return sceneAsset == null ? string.Empty : AssetDatabase.GetAssetPath(sceneAsset);
00108         }
00109 
00110         private void HandleBeforeSerialize()
00111         {
00112             // Asset is invalid but have Path to try and recover from
00113             if (IsValidSceneAsset == false && string.IsNullOrEmpty(scenePath) == false)
00114             {
00115                 sceneAsset = GetSceneAssetFromPath();
00116                 if (sceneAsset == null) scenePath = string.Empty;
00117 
00118                 EditorSceneManager.MarkAllScenesDirty();
00119             }
00120             // Asset takes precendence and overwrites Path
00121             else
00122             {
00123                 scenePath = GetScenePathFromAsset();
00124             }
00125         }
00126 
00127         private void HandleAfterDeserialize()
00128         {
00129             EditorApplication.update -= HandleAfterDeserialize;
00130             // Asset is valid, don't do anything - Path will always be set based on it when it matters
00131             if (IsValidSceneAsset) return;
00132 
00133             // Asset is invalid but have path to try and recover from
00134             if (string.IsNullOrEmpty(scenePath)) return;
00135 
00136             sceneAsset = GetSceneAssetFromPath();
00137             // No asset found, path was invalid. Make sure we don't carry over the old invalid path
00138             if (!sceneAsset) scenePath = string.Empty;
00139 
00140             if (!Application.isPlaying) EditorSceneManager.MarkAllScenesDirty();
00141         }
00142 #endif
00143     }
