00265 
00266                     if (DrawUtils.ButtonHelper(buttonRect, stateString, stateString + " In Build", EditorStyles.miniButtonLeft, tooltipMsg))
00267                         BuildUtils.SetBuildSceneState(buildScene, !isEnabled);
00268                     buttonRect.x += buttonRect.width;
00269 
