00276             buttonRect.x += buttonRect.width;
00277 
00278             tooltipMsg = "Open the 'Build Settings' Window for managing scenes." + readOnlyWarning;
00279             if (DrawUtils.ButtonHelper(buttonRect, "Settings", "Build Settings", EditorStyles.miniButtonRight, tooltipMsg))
00280             {
00281                 BuildUtils.OpenBuildSettings();
