00333         private static class BuildUtils
00334         {
00335             // time in seconds that we have to wait before we query again when IsReadOnly() is called.
00336             public static float minCheckWait = 3;
00337 
00338             private static float lastTimeChecked;
00339             private static bool cachedReadonlyVal = true;
00340 
00344             public struct BuildScene
00345             {
00346                 public int buildIndex;
00347                 public GUID assetGUID;
00348                 public string assetPath;
00349                 public EditorBuildSettingsScene scene;
00350             }
00351 
00356             public static bool IsReadOnly()
00357             {
00358                 var curTime = Time.realtimeSinceStartup;
00359                 var timeSinceLastCheck = curTime - lastTimeChecked;
00360 
00361                 if (!(timeSinceLastCheck > minCheckWait)) return cachedReadonlyVal;
00362 
00363                 lastTimeChecked = curTime;
00364                 cachedReadonlyVal = QueryBuildSettingsStatus();
00365 
00366                 return cachedReadonlyVal;
00367             }
00368 
00373             private static bool QueryBuildSettingsStatus()
00374             {
00375                 // If no version control provider, assume not readonly
00376                 if (!Provider.enabled) return false;
00377 
00378                 // If we cannot checkout, then assume we are not readonly
00379                 if (!Provider.hasCheckoutSupport) return false;
00380 
00382                 //if (UnityEditor.VersionControl.Provider.onlineState == UnityEditor.VersionControl.OnlineState.Offline)
00383                 //    return true;
00384 
00385                 // Try to get status for file
00386                 var status = Provider.Status("ProjectSettings/EditorBuildSettings.asset", false);
00387                 status.Wait();
00388 
00389                 // If no status listed we can edit
00390                 if (status.assetList == null || status.assetList.Count != 1) return true;
00391 
00392                 // If is checked out, we can edit
00393                 return !status.assetList[0].IsState(Asset.States.CheckedOutLocal);
00394             }
00395 
00399             public static BuildScene GetBuildScene(Object sceneObject)
00400             {
00401                 var entry = new BuildScene
00402                 {
00403                     buildIndex = -1,
00404                     assetGUID = new GUID(string.Empty)
00405                 };
00406 
00407                 if (sceneObject as SceneAsset == null) return entry;
00408 
00409                 entry.assetPath = AssetDatabase.GetAssetPath(sceneObject);
00410                 entry.assetGUID = new GUID(AssetDatabase.AssetPathToGUID(entry.assetPath));
00411 
00412                 var scenes = EditorBuildSettings.scenes;
00413                 for (var index = 0; index < scenes.Length; ++index)
00414                 {
00415                     if (!entry.assetGUID.Equals(scenes[index].guid)) continue;
00416 
00417                     entry.scene = scenes[index];
00418                     entry.buildIndex = index;
00419                     return entry;
00420                 }
00421 
00422                 return entry;
00423             }
00424 
00428             public static void SetBuildSceneState(BuildScene buildScene, bool enabled)
00429             {
00430                 var modified = false;
00431                 var scenesToModify = EditorBuildSettings.scenes;
00432                 foreach (var curScene in scenesToModify.Where(curScene => curScene.guid.Equals(buildScene.assetGUID)))
00433                 {
00434                     curScene.enabled = enabled;
00435                     modified = true;
00436                     break;
00437                 }
00438                 if (modified) EditorBuildSettings.scenes = scenesToModify;
00439             }
