00444             public static void AddBuildScene(BuildScene buildScene, bool force = false, bool enabled = true)
00445             {
00446                 if (force == false)
00447                 {
00448                     var selection = EditorUtility.DisplayDialogComplex(
00449                         "Add Scene To Build",
00450                         "You are about to add scene at " + buildScene.assetPath + " To the Build Settings.",
00451                         "Add as Enabled", // option 0
00452                         "Add as Disabled", // option 1
00453                         "Cancel (do nothing)"); // option 2
00454 
00455                     switch (selection)
00456                     {
00457                         case 0: // enabled
00458                             enabled = true;
00459                             break;
00460                         case 1: // disabled
00461                             enabled = false;
00462                             break;
00463                         default:
00464                             //case 2: // cancel
00465                             return;
00466                     }
00467                 }
00468 
00469                 var newScene = new EditorBuildSettingsScene(buildScene.assetGUID, enabled);
00470                 var tempScenes = EditorBuildSettings.scenes.ToList();
00471                 tempScenes.Add(newScene);
00472                 EditorBuildSettings.scenes = tempScenes.ToArray();
00473             }
00474 
00478             public static void RemoveBuildScene(BuildScene buildScene, bool force = false)
00479             {
00480                 var onlyDisable = false;
00481                 if (force == false)
00482                 {
00483                     var selection = -1;
00484 
00485                     var title = "Remove Scene From Build";
00486                     var details = $"You are about to remove the following scene from build settings:\n    {buildScene.assetPath}\n    buildIndex: {buildScene.buildIndex}\n\nThis will modify build settings, but the scene asset will remain untouched.";
00487                     var confirm = "Remove From Build";
00488                     var alt = "Just Disable";
00489                     var cancel = "Cancel (do nothing)";
00490 
00491                     if (buildScene.scene.enabled)
00492                     {
00493                         details += "\n\nIf you want, you can also just disable it instead.";
00494                         selection = EditorUtility.DisplayDialogComplex(title, details, confirm, alt, cancel);
00495                     }
00496                     else
00497                     {
00498                         selection = EditorUtility.DisplayDialog(title, details, confirm, cancel) ? 0 : 2;
00499                     }
00500 
00501                     switch (selection)
00502                     {
00503                         case 0: // remove
00504                             break;
00505                         case 1: // disable
00506                             onlyDisable = true;
00507                             break;
00508                         default:
00509                             //case 2: // cancel
00510                             return;
00511                     }
00512                 }
00513 
00514                 // User chose to not remove, only disable the scene
00515                 if (onlyDisable)
00516                 {
00517                     SetBuildSceneState(buildScene, false);
00518                 }
00519                 // User chose to fully remove the scene from build settings
00520                 else
00521                 {
00522                     var tempScenes = EditorBuildSettings.scenes.ToList();
00523                     tempScenes.RemoveAll(scene => scene.guid.Equals(buildScene.assetGUID));
00524                     EditorBuildSettings.scenes = tempScenes.ToArray();
00525                 }
00526             }
